import React, { useState, useEffect } from 'react';

// App에서 컴포넌트로 내리면 한번에 변경되서 포기하고 functional 으로 이전
function WorldWideWeb() {
    let [slide, setSlide] = useState(0);

    // componentDidMount
    useEffect(() => {
        var s = slide;
        var slnum = "slide" + s;
        var current = document.getElementById('currentSlide');
        var change = document.getElementById(slnum);
        current.innerHTML = change.innerHTML;
    }, []);
        
    // Next 할 때 페이지 제한 걸기 위한 슬라이드 갯수 세는 함수
    // return값은 마지막 슬라이드가 9번이라 치면 10으로 반환됨 (함수 사용에 쉽게 하려고)
    const lengthCheck = () => {
        var i = 0;
        for (i=0; ; i++) {
            var slnum = "slide" + i;
            if (!document.getElementById(slnum)) {
                break;
            }
        }
        return i;
    }

    // printView
    const printView = () => {
        var length = lengthCheck();
        var current = document.getElementById('currentSlide');
        current.innerHTML = "";
        for (var i=0; i<length; i++) {
            var slnum = "slide" + i;
            var change = document.getElementById(slnum);
            current.innerHTML += change.innerHTML;
        }

        var el = document.getElementById("jumplist");
        el.selectedIndex = 0;
        setSlide(0);
    };

    // Slide Next
    const moveSlideNext = () => {
        var length = lengthCheck();
        var s = parseInt(slide) + 1;
        if (s < length) {
            var slnum = "slide" + s;
            var current = document.getElementById('currentSlide');
            var change = document.getElementById(slnum);
            current.innerHTML = change.innerHTML;

            var el = document.getElementById("jumplist");
            el.selectedIndex = s;

            setSlide(s);
        } else {
            alert("Next page");
        }
    };

    // Slide Privious
    const moveSlidePrevious = () => {
        var s = parseInt(slide);
        if (s > 0) {
            s = s - 1;
            var slnum = "slide" + s;
            var current = document.getElementById('currentSlide');
            var change = document.getElementById(slnum);
            current.innerHTML = change.innerHTML;

            var el = document.getElementById("jumplist");
            el.selectedIndex = s;

            setSlide(s);
        } else {
            alert("First page");
        }
    };

    const jumpSlide = () => {     
        var el = document.getElementById("jumplist");
        var jumplist = el.options[el.selectedIndex].value;
        var s = getNum(jumplist);
        var slnum = "slide" + s;
        var current = document.getElementById('currentSlide');
        var change = document.getElementById(slnum);
        current.innerHTML = change.innerHTML;
        setSlide(s);
    }

    const getNum = (String) => {
        var num = "";
        for (var i=0; i < String.length; i++) {
            if (isNumber(String[i])) {
                num += String[i]
            } else {
                break;
            }
        }

        return num;
    }

    function isNumber(s) {
        s += ''; // 문자열로 변환
        s = s.replace(/^\s*|\s*$/g, ''); // 좌우 공백 제거
        if (s == '' || isNaN(s)) return false;
        return true;
    }

