import React from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
 
import Notifications, { successm, removeAll } from 'react-notification-system-redux';
 
const notificationOpts = {
  // uid: 'once-please', // you can specify your own uid if required
  title: 'Hey, it\'s good to see you!',
  message: 'Now you can see how easy it is to use notifications in React!',
  position: 'tr',
  autoDismiss: 0,
  action: {
    label: 'Click me!!',
    callback: () => alert('clicked!')
  }
};
 
class NotiContents extends React.Component {
 
  constructor() {
    super();
 
    this.handleClick = this.handleClick.bind(this);
  }
 
  handleClick() {
    this.context.store.dispatch(
      success(notificationOpts)
    );
  }
 
  render() {
    return (
      <div>
        <button onClick={this.handleClick}>
          Spawn some notifications!!!
        </button>
      </div>
    );
  }
}
 
NotiContents.contextTypes = {
  store: PropTypes.object
};
 
export default NotiContents;
