import React, {useState} from 'react';
import 'react-bootstrap';
import Problem from './Problem';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

import {Answerset} from './Problem';

// Popup으로 문제 내기

class Popmodal extends React.Component {
    constructor() {
        super();
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(event.target.S_id.value);
        console.log(event.target.A_sel.value);
        Answerset.Sid = event.target.S_id.value;
        Answerset.Asel = event.target.A_sel.value;
        console.log("Answerset");
        console.log(Answerset);
    }

    state = {
        show: false,
        setShow: false
    }

    // [show, setShow] = useState(false);

    handleClose = () => setState(() => { show: false});
    handleShow = () => setState(() => {show: true});

    // const handleClose = () => setShow(false);
    // const handleShow = () => setShow(true);

    render() {
        return (
            <div className='Popmodal'>
                <Button variant="primary" onClick={handleShow}>
                    Launch Problem
                </Button>

                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                    <Modal.Title>Problem</Modal.Title>
                    </Modal.Header>
                    <Modal.Body><Problem></Problem></Modal.Body>
                    <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        Submit
                    </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}

export default Popmodal;
